ies %}\n        {% if category.children %}\n        &lt;li class=&quot;dropdown&quot;&gt;&lt;a href=&quot;{{ category.href }}&quot; class=&quot;dropdown-toggle&quot; data-toggle=&quot;dropdown&quot;&gt;{{ category.name }}&lt;/a&gt;\n          &lt;div class=&quot;dropdown-menu&quot;&gt;\n            &lt;div class=&quot;dropdown-inner&quot;&gt; {% for children in category.children|batch(category.children|length / category.column|round(1, \'ceil\')) %}\n              &lt;ul class=&quot;list-unstyled&quot;&gt;\n                {% for child in children %}\n                &lt;li&gt;&lt;a href=&quot;{{ child.href }}&quot;&gt;{{ child.name }}&lt;/a&gt;&lt;/li&gt;\n                {% endfor %}\n              &lt;/ul&gt;\n              {% endfor %}&lt;/div&gt;\n            &lt;a href=&quot;{{ category.href }}&quot; class=&quot;see-all&quot;&gt;{{ text_all }} {{ category.name }}&lt;/a&gt; &lt;/div&gt;\n        &lt;/li&gt;\n        {% else %}\n        &lt;li class=&quot;&quot;&gt;&lt;a href=&quot;{{ category.href }}&quot;&gt;{{ category.name }}&lt;/a&gt;&lt;/li&gt;\n        {% endif %}\n        {% endfor %}\n        \n\n       \n      &lt;/ul&gt;\n              &lt;div id=&quot;search&quot; class=&quot;input-group&quot;&gt;\n  &lt;input name=&quot;search&quot; value=&quot;&quot; placeholder=&quot;Search&quot; class=&quot;form-control input-lg&quot; type=&quot;text&quot;&gt;\n  &lt;span class=&quot;input-group-btn&quot;&gt;\n    &lt;button type=&quot;button&quot; class=&quot;btn btn-default btn-lg&quot;&gt;&lt;i class=&quot;fa fa-search&quot;&gt;&lt;/i&gt;&lt;/button&gt;\n  &lt;/span&gt;\n&lt;/div&gt;\n    &lt;/div&gt;\n  &lt;/nav&gt;\n&lt;/div&gt;\n{% endif %} ','2018-07-12 16:36:44'),(242,0,'default','product/category','{{ header }}\n&lt;div id=&quot;product-category&quot; class=&quot;container&quot;&gt;\n  &lt;ul class=&quot;breadcrumb&quot;&gt;\n    {% for breadcrumb in breadcrumbs %}\n    &lt;li&gt;&lt;a href=&quot;{{ breadcrumb.href }}&quot;&gt;{{ breadcrumb.text }}&lt;/a